function() {
    var ecommerceItems = {{dlv - ecommerce.items}};
    var itemIDs = {{const - Google Ads - Product ID Format}};

    return ecommerceItems.map(function(item) {
        var idValue;

        switch (itemIDs) {
            case 'product_sku_ads':
                idValue = item.item_sku;
                break;
            case 'product_id_ads':
                idValue = item.item_id;
                break;
            case 'variant_id_ads':
                idValue = item.item_variant_id;
                break;
            default:
                idValue = undefined;
        }

        var newItem = Object.assign({}, item);
        newItem.item_id = idValue;
        return newItem;
    });
}
